import { SuiClient, getFullnodeUrl } from "@mysten/sui/client";
let apiKey = "";
let suiClient = new SuiClient({
    url: getFullnodeUrl("mainnet"),
});
function setApiKey(key) {
    apiKey = key;
}
function getApiKey() {
    return apiKey;
}
function getSuiClient() {
    return suiClient;
}
function setSuiClient(client) {
    suiClient = client;
}
const Config = {
    setApiKey,
    getApiKey,
    setSuiClient,
    getSuiClient,
};
export { Config };
